import React, { useEffect, useState } from "react";
import Skeleton from "react-loading-skeleton";
import 'react-loading-skeleton/dist/skeleton.css'
import { NavLink, useParams } from "react-router-dom";

export default function Product() {
  const { id } = useParams();
  const [product, setProduct] = useState([]);
  const [loading, setLoading] = useState(false);
  useEffect(() => {
    const getProduct = async () => {
      setLoading(true);
      const response = await fetch(`https://fakestoreapi.com/products/${id}`);
      setProduct(await response.json());
      setLoading(false);
      console.log(product);
    };
    getProduct();
  }, []);
  const Loading = () => {
    return (
        <>
        <div className="col-md-6">
            <Skeleton height={400}/>
        </div>
        <div className="col-md-6">
            <Skeleton height={50} width={300}/>  
            <Skeleton height={75} width={300}/>  
            <Skeleton height={25} width={300}/>  
            <Skeleton height={100} width={450}/>  
            <Skeleton height={200} width={450}/>  
            <Skeleton height={50} width={450}/>  
        </div>
        </>
    );
  };
  const ShowProduct = () => {
    return (
      <>
        <div className="col-md-6 col-sm-2 ">
          <img
            src={product.image}
            alt={product.title}
            height="400px"
            width="400px"
          />
        </div>
        <div className="col-md-6 ">
          <h4 className="text-uppercase text-black-50">{product.category}</h4>
          <h1 className="display-5">{product.title}</h1>
          <p className="lead fw-bolder">
            Rating {product.rating && product.rating.rate} 
            <span className="bi bi-star-fill text-warning ms-2"></span>
            </p>
            <h3 className="display-6 fw-bold my-4">
                $ {product.price}
            </h3>
            <p className="lead">
                {product.description}
            </p>
            <button className="btn btn-outline-primary px-4 py-2">Add to Cart</button>
            <NavLink to="/cart" className="btn btn-outline-primary ms-2 px-3 py-2 ">
                Go to Cart
            </NavLink>
        </div>
      </>
    );
  };
  return (
    <div>
      <div className="container py-5">
        <div className="row py-5">{loading ? <Loading /> : <ShowProduct />}</div>
      </div>
    </div>
  );
}
